sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// Create GlideDate objects for the current date and the passed in date.
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		// If the testDate is before rightNow, return true, else return false
		if (testDate.before(rightNow)){
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate){
		// Create GlideDate objects for the current date and the passed in date and extract 
		// the local date without timestamps.
		var today = (new GlideDateTime()).getLocalDate();
		var testDate = (new GlideDateTime(strDate)).getLocalDate();

		// Compare today and testDate to see if they are the same date. If so, return true,
		// else return false.
		if (today.compareTo(testDate) == 0){
			return true;
		}
		else {
			return false;
		}

	},

    type: 'NeedItUtils'
};
